package loop;

public class Foreachloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int sum=0; //value of sum
int arr[]= {1,2,3,4,5};//arrray value
for(int i :arr) {
	System.out.println(i);//o/p
	sum=sum+i;
}
	System.out.println(sum);//result

	}

}
