package switch_break;

public class Switch_test {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=10;// declaring
		switch(num) {
		case 1:System.out.println("12");
		break;
		case 12:System.out.println("15");
		break;
		case 10:System.out.println("good");
		break;
		case 51:System.out.println("51");
		break;
		
default : System.out.println("not with in given condition");
	}
	}
}
